// ReadableState  59
function ReadableState(options, stream) {//optionsReadable
  options = options || {};//optionsoptions

  // Duplex streams are both readable and writable, but share
  // the same options object.
  // However, some cases require setting options to different
  // values for the readable and the writable sides of the duplex stream.
  // These options can be provided separately as readableXXX and writableXXX.
  var isDuplex = stream instanceof Stream.Duplex;//

  // object stream flag. Used to make read(n) ignore n and to
  // make all the buffer merging and length checks go away
  this.objectMode = !!options.objectMode;//readn

  if (isDuplex)//this.objectMode
    this.objectMode = this.objectMode || !!options.readableObjectMode;

  // the point at which it stops calling _read() to fill the buffer
  // Note: 0 is a valid value, means "don't call _read preemptively ever"
  var hwm = options.highWaterMark;//highWaterMark
  var readableHwm = options.readableHighWaterMark;//highWaterMark
  var defaultHwm = this.objectMode ? 16 : 16 * 1024;//highWaterMark

  if (hwm || hwm === 0)
    this.highWaterMark = hwm;//highWaterMark0highWaterMark
  else if (isDuplex && (readableHwm || readableHwm === 0))
    this.highWaterMark = readableHwm;//highWaterMarkhighWaterMark
  else
    this.highWaterMark = defaultHwm;//this.highWaterMark

  // cast to ints.
  this.highWaterMark = Math.floor(this.highWaterMark);//

  // A linked list is used to store data chunks instead of an array because the
  // linked list can remove elements from the beginning faster than
  // array.shift()
  this.buffer = new BufferList();//BufferList,
  this.length = 0;//
  this.pipes = null;//
  this.pipesCount = 0;//
  this.flowing = null;//
  this.ended = false;//Readabletrue
  this.endEmitted = false;//Readabletrueended
  this.reading = false;//_read

  // a flag to be able to tell if the event 'readable'/'data' is emitted
  // immediately, or on a later tick.  We set this to true at first, because
  // any actions that shouldn't happen until "later" should generally also
  // not happen before the first read call.
  this.sync = true;//emitReadable

  // whenever we return null, then we set a flag to say
  // that we're awaiting a 'readable' event emission.
  this.needReadable = false;//Readable
  this.emittedReadable = false;//Readable
  this.readableListening = false;//
  this.resumeScheduled = false;

  // has it been destroyed
  this.destroyed = false;//

  // Crypto is kind of old and crusty.  Historically, its default string
  // encoding is 'binary' so we have to make this configurable.
  // Everything else in the universe uses 'utf8', though.
  this.defaultEncoding = options.defaultEncoding || 'utf8';//Encoding

  // the number of writers that are awaiting a drain event in .pipe()s
  this.awaitDrain = 0;//pipe

  // if true, a maybeReadMore has been scheduled
  this.readingMore = false;//readable

  //
  this.decoder = null;//
  this.encoding = null;//
  if (options.encoding) {
